import { useState } from "react";


export default function CreateCard() {
  const [fullName, setFullName] = useState("");
  const [aboutMe, setAboutme] = useState("");
  const [html, setHTML] = useState(false);
  const [css, setCSS] = useState("");
  const [js, setJS] = useState("");
  const [git, setGit] = useState("");
  const [react, setReact] = useState("");
  const [node, setNode] = useState("");

  const [githubUrl, setGithubUrl] = useState("");
  const [favouriteBooks, setFavouriteBooks] = useState("");


  return (
  <div>
    <h1>Create Your DevCard</h1>
    <form>
      <input onChange = {(e) => 
        setFullName(e.target.value)
       } type="text" placeholder="Your Full Name"
      />

      <input onChange={(e) => setAboutme(e.target.value)}
      type="text" placeholder="About me"/>

       <h2>Technologies you Know:</h2>
       <input onClick={(e) => setHTML(true)} type="checkbox" name="HTML"/>
       <label for="HTML">HTML</label>

      <input onClick={(e) => setCSS(e.target.value)} type="checkbox" name="CSS"/>
      <label for="CSS">CSS</label>

      <input onClick={(e) => setJS(e.target.value)} type="checkbox" name="JS"/>
      <label for="JS">JS</label>

      <input onClick={(e) => setGit(e.target.value)} type="checkbox" name="git"/>
      <label for="git">Git</label>

      <input onClick={(e) => setReact(e.target.value)} type="checkbox" name="react"/>
      <label for="react">React</label>

      <input onClick={(e) => setNode(e.target.value)} type="checkbox" name="Node.JS"/>
      <label for="Node.JS">Node.JS</label>

      <input onChange={(e) => setGithubUrl(e.target.value)} type="text" placeholder="Github URL"></input>

      <input onChange={(e) => setFavouriteBooks(e.target.value)} type="text" placeholder="Favourte Books (Separate by comma)"></input>


    </form>

    <p>Fullname: {fullName}</p>
    <p>About me: {aboutMe}</p>
    <p>HTML: {html}</p>
    <p>CSS: {css}</p>
    <p>JS: {js}</p>
    <p>Git: {git}</p>
    <p>React: {react}</p>
    <p>Node.JS: {node}</p>
    <p>GitHub URL: {githubUrl}</p>
    <p>Favourite Book: {favouriteBooks}</p>
  </div>);
}
